package qx

import (
	"bytes"
	"encoding/hex"
	"github.com/stretchr/testify/require"
	"testing"
)

func Test_EntityOrders_UnmarshallFromReader(t *testing.T) {

	entityAskOrdersHex := ""
	ordersBin, decodeErrors := hex.DecodeString(entityAskOrdersHex)
	require.NoError(t, decodeErrors, "binary marshalling payload")

	var result EntityOrders
	unmarshallErrors := result.UnmarshallFromReader(bytes.NewReader(ordersBin))
	require.NoError(t, unmarshallErrors)

	if len(result) != 18 {
		t.Errorf("Unexpected number of orders: [%d]", len(result))
	}

	var testOrder EntityOrder = result[2]
	var expectedName = []byte{'M', 'L', 'M', 0, 0, 0, 0, 0}

	if !bytes.Equal(expectedName, testOrder.AssetName[:]) {
		t.Errorf("Order.AssetName expected [%s], got [%s]", expectedName, testOrder.AssetName)
	}

	if testOrder.Price != 700000000 {
		t.Errorf("Incorrect price. Got [%d]", testOrder.Price)
	}

	if testOrder.NumberOfShares != 2 {
		t.Errorf("Incorrect number of shares. Got [%d]", testOrder.NumberOfShares)
	}

}

func Test_AssetOrders_UnmarshallFromReader(t *testing.T) {

	assetBidOrders := ""
	ordersBin, decodeErr1 := hex.DecodeString(assetBidOrders)
	require.NoError(t, decodeErr1, "binary marshalling payload")

	var result AssetOrders
	unmarshallErrors := result.UnmarshallFromReader(bytes.NewReader(ordersBin))
	require.NoError(t, unmarshallErrors)

	entity, decodeErr2 := hex.DecodeString("069593064b22d9996b656bd632fd9e100e6ac992dfb201204cd3f10fd66ce3ff")
	require.NoError(t, decodeErr2)

	if len(result) != 33 {
		t.Errorf("Unexpected number of orders: [%d]", len(result))
	}

	var testOrder AssetOrder = result[0]
	if testOrder.Entity != [32]byte(entity) {
		t.Errorf("Unexpected entity. Got [%v]", testOrder.Entity)
	}

	if testOrder.Price != 3 {
		t.Errorf("Incorrect price. Got [%d].", testOrder.Price)
	}

	if testOrder.NumberOfShares != 1570495966 {
		t.Errorf("Incorrect number of shares. Got [%d].", testOrder.NumberOfShares)
	}

}
